Template.Design:
@using System.Linq
@using TeaCommerce.Api.Models
@using TeaCommerce.Umbraco.Web
@using Umbraco.Core.Models
@using Umbraco.Web
@using ZeroCostPayment.Models.Settings
@using ZeroCostPayment.Services

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
  Layout = "CartStepMaster.cshtml";

  IPublishedContent currentPage = Model.Content;
  IPublishedContent nextStep = currentPage.FollowingSibling();
  IPublishedContent prevStep = currentPage.PrecedingSibling();

  //store id
  long storeId = long.Parse( currentPage.GetPropertyValue<string>( "store", true ) );
  Order order = TC.GetCurrentOrder( storeId, false );

  ZeroCostPaymentConfig config = ConfigReaderService.Instance.GenericDeSerialize<ZeroCostPaymentConfig>( "~/Config/ZeroCostPayment.config" );
  ZeroCostPaymentSetting storeSetting = config.Settings.FirstOrDefault( setting => setting.StoreId == storeId );

  long zeroCostPaymentId = storeSetting != null ? storeSetting.PaymentMethodId : 0;
}
@if ( order != null ) {
  <div class="container cartContainer">
    <h1>Choose shipping and payment</h1>
    <form action="/base/TC/FormPost.aspx" method="post" class="form-horizontal form-autoValidate">
      <input name="SetCurrentPaymentMethod" value="paymentMethodId" type="hidden" />
      <input name="SetCurrentShippingMethod" value="shippingMethodId" type="hidden" />
      <input name="returnUrl" type="hidden" value="@nextStep.Url"/>
      <input name="storeId" value="@storeId" type="hidden" />

      <div class="row">
        @if ( order.PaymentInformation.PaymentMethodId != zeroCostPaymentId ) {
          <div class="col-sm-6">
            <div class="well">
              <h3>Select payment method</h3>
              @foreach ( PaymentMethod method in TC.GetPaymentMethods( storeId ).Where( method => method.Id != zeroCostPaymentId ) ) {
                <div class="radio">
                  <label>
                    <input type="radio" name="paymentMethodId" value="@method.Id"
                      @if ( method.Id == order.PaymentInformation.PaymentMethodId ) {
                        @Html.Raw( "checked" )
                      }>
                    @method.Name
                  </label>
                </div>
              }
            </div>
          </div>
        } else {
          <input type="hidden" name="paymentMethodId" value="@zeroCostPaymentId" />
        }
        <div class="col-sm-6">
          <div class="well">
            <h3>Select shipping method</h3>
            @foreach ( ShippingMethod method in TC.GetShippingMethods( storeId ) ) {
              <div class="radio">
                <label>
                  <input type="radio" name="shippingMethodId" value="@method.Id"
                    @if ( method.Id == order.ShipmentInformation.ShippingMethodId ) {
                      @Html.Raw( "checked" )
                    }>
                  @method.Name
                </label>
              </div>
            }
          </div>
        </div>
      </div>

      <nav class="navbar">
        <button type="submit" class="btn btn-success">Continue</button>
        <a href="@prevStep.Url" class="btn btn-default">Back</a>
      </nav>
    </form>
  </div>
}

@inherits UmbracoTemplatePage
@{
  Layout = "Master.cshtml";

  IPublishedContent currenContent = Model.Content,
    cart = currenContent.AncestorOrSelf( "Cart" );

  IReadOnlyList<IPublishedContent> cartSteps = cart.Children.Where( c => c.DocumentTypeAlias == "CartStep" ).ToList();
  int activeStep = cart.Id == currenContent.Id ? 1 : cart.Children.IndexOf( cartSteps.First( c => c.Id == currenContent.Id ) ) + 2;
}

<div class="container cartTop">
  <div class="input-group">
    @if ( activeStep > 1 ) {
      <a href="@cart.Url" class="input-group-addon">@cart.Name</a>
    } else {
      <span class="input-group-addon">@cart.Name</span>
    }
    @{
      int cartStepCount = 2;
      foreach ( IPublishedContent cartStep in cartSteps ) {
        string cartStepCssClass = "input-group-addon";
        cartStepCssClass += cartStepCount < activeStep ? " inpath" : "";
        cartStepCssClass += cartStepCount == activeStep ? " active" : "";
        if ( cartStepCount < activeStep ) {
          <a href="@cartStep.Url" class="@cartStepCssClass">@cartStep.Name</a>
        } else {
          <span class="@cartStepCssClass">@cartStep.Name</span>
        }
        cartStepCount++;
      }
    }
  </div>

</div>

@RenderBody()

@using System.Activities.Statements
@using System.Globalization
@using TeaCommerce.Api.Models
@using TeaCommerce.Umbraco.Web
@inherits UmbracoTemplatePage
@{
  Layout = "Master.cshtml";
  IPublishedContent currentPage = Model.Content;

  IReadOnlyList<IPublishedContent> carouselImages = currentPage.GetPropertyValue<string>( "slider" )
    .Split( new[] { ',' }, StringSplitOptions.RemoveEmptyEntries )
    .Select( int.Parse )
    .Select( id => Umbraco.TypedMedia( id ) )
    .Where( m => m != null )
    .ToList();

  int count = 0;
  string carouselId = "homeCarousel";

  //store id
  long storeId = long.Parse( currentPage.GetPropertyValue<string>( "store", true ) );

  List<IPublishedContent> products = currentPage.GetPropertyValue<string>( "featuredProducts" )
    .Split( new[] { ',' }, StringSplitOptions.RemoveEmptyEntries )
    .Select( int.Parse )
    .Select( id => Umbraco.TypedContent( id ) )
    .Where( c => c != null )
    .ToList();
}
@if ( carouselImages.Any() ) {
  <div id="@carouselId" class="carousel slide" data-ride="carousel">
    <!-- Indicators -->
    <ol class="carousel-indicators">
      @foreach ( IPublishedContent image in carouselImages ) {
        <li data-target="#@carouselId" data-slide-to="@count" class="@(count == 0 ? "active" : "")"></li>
        count++;
      }
    </ol>

    <!-- Wrapper for slides -->
    <div class="carousel-inner" role="listbox">
      @{
      count = 0;
      foreach ( IPublishedContent image in carouselImages ) {
        <div class="item @(count == 0 ? "active" : "")">
          <img src="@image.Url?width=2000&heightratio=0.5&mode=crop" alt="@image.Name">
        </div>
        count++;
      }
      }

    </div>

    <!-- Controls -->
    <a class="left carousel-control" href="#@carouselId" role="button" data-slide="prev">
      <span class="glyphicon glyphicon-chevron-left" aria-hidden="true"></span>
      <span class="sr-only">Previous</span>
    </a>
    <a class="right carousel-control" href="#@carouselId" role="button" data-slide="next">
      <span class="glyphicon glyphicon-chevron-right" aria-hidden="true"></span>
      <span class="sr-only">Next</span>
    </a>
  </div>
}

<div class="container">
  <div class="row">
    @foreach ( IPublishedContent product in products ) {
      var name = product.GetPropertyValue<string>( "name", true );
      IPublishedContent firstVariant = product.Children.FirstOrDefault();
      Price price = TC.GetPrice( storeId, product.Id.ToString( CultureInfo.InvariantCulture ) );
      IPublishedContent image = null;

      int imageId = firstVariant != null ? firstVariant.GetPropertyValue<int>( "image", true ) : product.GetPropertyValue<int>( "image" );
      if ( imageId != 0 ) {
        image = Umbraco.TypedMedia( imageId );
      }

      <div class="col-sm-4 col-md-3">
        <div class="product well well-sm text-center">
          @if ( image != null ) {
            <a href="@product.Url">
              <img class="img-responsive img-thumbnail" src="@image.Url?width=300&heightratio=0.75&mode=crop"/>
            </a>
          }
          <h3>@name</h3>
          <p>@price.Formatted</p>
          <a href="@product.Url" class="btn btn-primary btn-block">View more</a>
        </div>
      </div>
    }
  </div>
</div>

@using TeaCommerce.Api.Models
@using TeaCommerce.Umbraco.Configuration.Variant
@using TeaCommerce.Umbraco.Configuration.Variant.Product
@using TeaCommerce.Umbraco.Web
@using Umbraco.Core.Models
@using Umbraco.Web
@using System.Linq;

@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
  Layout = "Master.cshtml";

  IPublishedContent productPage = Model.Content;

  //store id
  long storeId = long.Parse( productPage.GetPropertyValue<string>( "store", true ) );

  //Main product data
  var productName = productPage.GetPropertyValue<string>( "name" );
  var productDescription = new HtmlString( productPage.GetPropertyValue<string>( "description" ) );

  IPublishedContent productImage = null;

  var productimageId = productPage.GetPropertyValue<int>( "image" );
  if ( productimageId != 0 ) {
    productImage = Umbraco.TypedMedia( productimageId );
  }

  List<VariantPublishedContent> variants = TC.GetVariants( storeId, productPage, false ).ToList();

  List<VariantGroup> attributeGroups = TC.GetVariantGroups( variants ).ToList();

  string variantJson = TC.GetVariantJson( storeId, productPage, true );
}

<script type="text/javascript">
  var _products = @Html.Raw( variantJson );
</script>

<div class="container productView">
  <div class="row">
    <div class="col-sm-8">
      <div class="productImages">
        @if ( productImage != null ) {
          <img class="img-responsive" src="@productImage.Url?width=750"/>
        }
      </div>
    </div>
    <div class="col-sm-4">
      <form method="post" action="/base/TC/FormPost.aspx" class="ajaxForm" data-product-id="@productPage.Id">
        <input name="AddOrUpdateOrderLine" value="productIdentifier, quantity" type="hidden" />
        <input name="productIdentifier" value="" type="hidden" />

        <input name="storeId" value="@storeId" type="hidden" />

        <div class="well">
          <h1 class="page-header">@productName</h1>

          @Html.Raw( productDescription )

          <div class="form-horizontal">
            @foreach ( VariantGroup variantGroup in attributeGroups ) {
              <div class="form-group">
                <label class="col-sm-3 control-label">@variantGroup.Name</label>
                <div class="col-sm-9">
                  <select class="form-control" data-attribute-group="@variantGroup.Id">
                    <option value="">Select @variantGroup.Name</option>
                    @foreach ( VariantType variantType in variantGroup.Attributes ) {
                      <option value="@variantType.Id">@variantType.Name</option>
                    }
                  </select>
                </div>
              </div>   
            }
            <div class="form-group">
              <label class="col-sm-3 control-label">Quantity</label>
              <div class="col-sm-9">
                <input class="form-control" name="quantity" value="1" type="text" />
              </div>
            </div>
          </div>
          <input class="btn btn-primary btn-block disabled" value="Add to basket" type="submit" />
        </div>
      </form>
    </div>
  </div>
</div>

@using System.Globalization
@using TeaCommerce.Api.Models
@using TeaCommerce.Umbraco.Web
@inherits UmbracoTemplatePage
@{
  Layout = "Master.cshtml";
  IPublishedContent currentPage = Model.Content;

  //store id
  long storeId = long.Parse( currentPage.GetPropertyValue<string>( "store", true ) );

  var headline = currentPage.GetPropertyValue<string>( "umbHeadline" );

  List<IPublishedContent> products = currentPage.Children.ToList();
}
<div class="container">
  <h1 class="page-header">@headline</h1>
  <div class="row">
    @foreach ( IPublishedContent product in products ) {
      var name = product.GetPropertyValue<string>( "name", true );
      IPublishedContent firstVariant = product.Children.FirstOrDefault();
      Price price = TC.GetPrice( storeId, product.Id.ToString( CultureInfo.InvariantCulture ) );
      IPublishedContent image = null;

      int imageId = firstVariant != null ? firstVariant.GetPropertyValue<int>( "image", true ) : product.GetPropertyValue<int>( "image" );
      if ( imageId != 0 ) {
        image = Umbraco.TypedMedia( imageId );
      }

      <div class="col-sm-4 col-md-3">
        <div class="product well well-sm text-center">
          @if ( image != null ) {
            <a href="@product.Url">
              <img class="img-responsive img-thumbnail" src="@image.Url?width=300&heightratio=0.75&mode=crop"/>
            </a>
          }
          <h3>@name</h3>
          <p>@price.Formatted</p>
          <a href="@product.Url" class="btn btn-primary btn-block">View more</a>
        </div>
      </div>
    }
  </div>
</div>

@using System
@using System.Collections.Generic
@using System.Globalization
@using System.Linq
@using System.Web
@using Examine
@using TeaCommerce.Api.Models
@using TeaCommerce.Umbraco.Web
@using umbraco.MacroEngines
@using Umbraco.Core.Models
@using Umbraco.Web
@inherits Umbraco.Web.Mvc.UmbracoTemplatePage
@{
  Layout = "Master.cshtml";

  IPublishedContent currentPage = Model.Content;

  //store id
  long storeId = 1;

  //Product
  string ProductTypeName = "Product";

  string searchUrl = currentPage.AncestorOrSelf( "Lang" ).Descendants( "ProductSearchPage" ).First().Url;

  string searchString = HttpContext.Current.Request.QueryString[ "q" ] ?? "";

  IEnumerable<SearchResult> searchResults = ExamineManager.Instance.SearchProviderCollection[ "ProductSearcher" ].Search( searchString, true ).OrderByDescending( s => s.Score );

  UmbracoHelper helper = new UmbracoHelper( UmbracoContext.Current );
}

<div class="container">
  <form action="@searchUrl" method="get" class="form-horizontal">
    <div class="form-group">
      <div class="col-sm-3">
        <input class="form-control" name="q" type="text" value="@Request.QueryString[ "q" ]"/>
      </div>
      <div class="col-sm-1">
        <input class="form-control" type="submit" value="Search" />
      </div>
    </div>
  </form>
</div>


<div class="container">
  <div class="row">
    @foreach ( SearchResult product in searchResults ) {
      var name = product.Fields[ "productName" ];
      var thisProduct = helper.TypedContent( product.Id );
      IPublishedContent firstVariant = thisProduct.Children.FirstOrDefault();
      Price price = TC.GetPrice( storeId, product.Id.ToString( CultureInfo.InvariantCulture ) );
      IPublishedContent image = null;

      int imageId = firstVariant != null ? firstVariant.GetPropertyValue<int>( "image", true ) : thisProduct.GetPropertyValue<int>( "image" );
      if ( imageId != 0 ) {
        image = Umbraco.TypedMedia( imageId );
      }

      <div class="col-sm-4 col-md-3">
        <div class="product well well-sm text-center">
          @if ( image != null ) {
            <a href="@thisProduct.Url">
              <img class="img-responsive img-thumbnail" src="@image.Url?width=300&heightratio=0.75&mode=crop"/>
            </a>
          }
          <h3>@name</h3>
          <p>@price.Formatted</p>
          <a href="@thisProduct.Url" class="btn btn-primary btn-block">View more</a>
        </div>
      </div>
    }
  </div>
</div>
